import base64, struct

def bmp_info(data):
    s = struct.unpack('<ccIIIIIIHH', data[:30])
    if s[0] == b'B' and s[1] in (b'M', b'A'):
        return {
            'width': s[6],
            'height': s[7],
            'color': s[9]
        }
    else:
        return None

bmp_data = base64.b64decode('')

# æµ‹è¯•
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
